    'f7': 118,
    'f8': 119,
    'f9': 120,
    'f10': 121,
    'f11': 122,
    'f12': 123
  },
  DEFAULT_OPTIONS: {
    'type': 'keydown',
    'propagate': false,
    'disableOnInput': false,
    'target': document,
    'keycode': false
  }
};

ShortcutManager.prototype.Shortcut = Shortcut;
ShortcutManager.prototype.add = function (shortcut, callback, options) {
  var shortcut = new this.Shortcut({
    shortcut: shortcut,
    callback: callback,
