  var form = new this.WebForm(config);
  this.forms.push(form);
  return form;
};
WebForms.prototype.find = function (id) {
  var form = {};

  for (var i = 0; i < this.forms.length; i++) {
    if (this.forms[i]._id == id) {
      form = this.forms[i];
    }
  }

  return form;
};
WebForms.prototype.remove = function (id) {
  var index = -1;

  for (var i = 0; i < this.forms.length; i++) {
    if (this.forms[i]._id === id) {
      index = i;
      break;
    }
  }
  if (index > -1) {
    this.forms.splice(index, 1);
  }
};
WebForms.prototype.show = function (form) {
  var webForm = this.find(form._id);
  console.log('WebForms: show -> ', webForm, form);

  if (webForm && webForm.hasOwnProperty('_id')) {
    webForm.load(form);
    this.active = webForm;
  } else {
    throw new Error('web-form not found!');
  }
};
